INSERT INTO `review` (`REV_HOS_SEQ`, `REV_TITLE`, `REV_USERID`, `REV_CONTENT`, `REV_DATE`, `REV_UP`, `REV_STAR`, `REV_PIC`, `REV_REQ`, `REV_INFO`, `REV_RES_DATE`, `REV_RES_TIME`, `REV_RES_SEQ`, `REV_HOS_ID`) VALUES
	(1, '가본 병원중에 제일 좋아요.', 'Aeeon1234', '과잉진료없고 꼭 필요한것은 말씀주시고, 같은 24시간인데 어느 병원처럼 병원비만 비싼곳보다 100배 천배나음.  외과과장님 너무 감사합니다. 이번에 구조한 아깽이 수술도 너무 잘해주셔서 보행에 문제없이 건강하게 크게됬어요.\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-04', '11:51:36', 81, 'songpahos'),
	(2, '감사합니다 정말..', 'Aeeon1234', '근 5년동안 피부병인줄 알고 피부약만 오랜시간 먹어왔는데 이번에 여기서 건강검진 받고 추가 검사에서 갑상선 호르몬질환 발견했어요.. 털도 많이 빠지고 각질도 심하게 날렸었는데 호르몬 약 먹은지 한달차, 각질은 거의 사라지고 털이 숭숭 빠졌던 부위는 조금씩 새 털들이 자라고있어요 ㅠㅠ 감동입니다.. 덕분에 울 애기 삶의 질이 향상했어요 ㅠㅠㅠ 사랑해요 비타민..ㅠㅠㅠ\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-03-23', '12:13:21', 86, 'marihos'),
	(5, ' 남아를 키우고 있습니다', 'mgdfdf13', '진료도 꼼꼼하게봐주시고 친절하시네요\r\n아이도 두려움없이 잘 오구요\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-03-20', '09:30:00', 3, 'dulyhos'),
	(6, '감사합니다 친절하시네요', 'mgdfdf13', '친절과 서비스가 훌륭합니다.\r\n울 장군이 입과 다리 수술받은곳.', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-17', '18:43:13', 60, 'eyeshos'),
	(7, '넘넘 고맙고 감사해요', 'bsdzxd123', '똘이가 갑자기 아파 ‥여기저기 ‥범백이라‥딴아가들 감염때문에 입원 불가라고‥여긴 격리실있어 6일째 입원. 치료 중~~~원장님과 선생님들이 잘해주셔서 퇴원해도 된다네요‥넘넘 고맙고 감사합니다~\r\n', '2023-05-21', 1, 5, NULL, 'B', NULL, '2023-03-20', '10:30:00', 4, 'haddenhos'),
	(8, '시설도 좋고 잘 봅니다.', 'bsdzxd123', '우리집 강쥐가 피부병이 심했는데 비타민에서 치료 받고 많이 좋아졌어요. 다른 곳에서는 못 고친다고 했는데.\r\n시설도 좋고 잘 봅니다.', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-03-24', '17:00:00', 39, 'songpahos'),
	(9, '자주자주방문하는곳', 'bsdzxd123', '친절하고 깔끔한 동물병원~! 병원가는 느낌보다는 동네잠깐 마실가는 느낌이예요!', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-04', '13:35:32', 85, 'onehos'),
	(10, '어이가없네요 정말', 'subin1234', '겉만 번드르하지 실력이 없네요. 우리 강아지가 수술받고 일주일째 설사 중인데 문제 해결을 못하더군요.', '2023-05-21', 0, 1, '푸들2.jpg', 'N', '추후에 상담을 통해 분쟁해결하였습니다. 반려 부탁드립니다.', '2023-03-21', '17:30:00', 15, 'dasomhos'),
	(11, '한번 읽어보세요', 'subin1234', '예약제 운영이고 진료비 저렴한데 진찰은 무난하게 해줍니다. 근데 의사고 직원이고 불친절한 편이에요. 특히 의사분 말투가 쏘아붙이는 듯한 말투에다가, 묻는 말에 ox만 듣고 싶어하는 타입. 사정설명은 잘 귀담아 듣지도 않고요. 같이 간 저희 엄마가 이것저것 물어보니까 귀찮아하는 태도에 기분이 안좋았어요. 사람과의 원만한 소통법을 잘 모르시는듯.. 그래도 동물병원 의사니까 동물들에게는 친절해서 다행이네요. 방문계획이 있다면 친절은 기대하지 마세요.\r\n', '2023-05-21', 5, 1, '푸들6.jpg', 'N', '부적합한 평점으로 판단되어 신고합니다.', '2023-03-21', '09:30:00', 17, 'akhos'),
	(12, '우너장님 제외하고는...', 'subin1234', '아~여긴 정말 윈장님이 짱인듯~그렇게 불친절해요~하는말마다 쫌 오쁘게 말을 않해요~우리 아기 병도 못고치고 촉오야~', '2023-05-21', 0, 1, NULL, 'N', '더욱 더 좋은 서비스로 보답하려고 합니다.', '2023-04-16', '12:52:48', 51, 'joenhos'),
	(13, '매우 친절하시고 꼼꼼히 챙겨주십니다', 'subin1234', '선생님이 매우 친절하시고 꼼꼼히 챙겨주십니다', '2023-05-21', 0, 4, '말티즈3.jpg', 'B', NULL, '2023-03-21', '10:00:00', 24, 'abchos'),
	(14, '좋아아요', 'subin1234', '은비,까비가 다니는 병원이예요 좋아요', '2023-05-21', 0, 4, '푸들4.jpg', 'B', NULL, '2023-03-25', '19:18:53', 89, 'alhos'),
	(15, '여기가 진짜', 'dasdsad1234', '앨리스원장님 진짜 좋음..위생신경쓰고, 친절진료하시고, 간호사쌤도 좋음. 무엇보다 진료비 많이 안비싸게 부담 덜 느끼게 신경 많이 써주십니다..', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-03-21', '16:30:00', 16, 'bundang'),
	(16, '감사해요', 'dasdsad1234', '노령견 관리를 못해줘서 치매가 온 줄도 몰랐네요. 선생님이 문진과 함께 설명을 자세히 해 주셔서 이해가 되었습니다.', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-03-22', '10:00:00', 29, 'bucheanhos'),
	(17, '브이동물병원짱', 'dasdsad1234', '친절하시고 자세한 설명과 섬세한 치료까지 ^^\r\n시설만 좋은 병원들과는 전혀 다릅니다!', '2023-05-21', 0, 5, NULL, 'N', NULL, '2023-04-15', '08:18:52', 61, 'vhos'),
	(18, '좋아요', 'cczxrw1234', '원장님 정말 꼼꼼하시고 친절하시죠^^', '2023-05-21', 0, 5, NULL, 'N', '신고이유 부적합하여 반려 부탁드려요', '2023-03-24', '10:00:00', 33, 'oasishos'),
	(19, '거품없이 친절해요', 'cczxrw1234', '거품없이 친절해요', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-15', '11:18:38', 62, 'happyhos'),
	(20, '그냥 좋음', 'cczxrw1234', '애완동물은 처음이라 다른 병원들을 몰라 비교는 힘들어요.\r\n친절하고 좋은데 가격은 조금 부담이 되는데 다른 곳과 비교가 안돼서 상대적으로 비싼지는 모르겠습니다.', '2023-05-21', 0, 4, NULL, 'B', NULL, '2023-04-11', '15:49:16', 72, 'eyeshos'),
	(21, '필요한 진료만 깔끔하게 잘 해주심.', 'vuskej54', '서비스 매우 친절. 접근성 굿. 궁물 다 답변해주심. 6살 토끼 여름에 개복 중성화••• 예후 굿. 건강하다고 나중에 이빨만 자세히 보자고, 필요한 진료만 깔끔하게 잘 해주심.\r\n', '2023-05-21', 0, 5, NULL, 'N', '신고합니다', '2023-04-15', '10:46:21', 68, 'daonhos'),
	(22, '헤헤 ', 'vuskej54', '친절하고 좋은 실력까지 갖춘 몇안되는 안심 병원입니다. 울 야옹이들 안심하고 키워요^^\r\n', '2023-05-21', 0, 4, NULL, 'B', NULL, '2023-03-25', '20:27:39', 84, 'haenhos'),
	(23, '넘넘 고맙고 감사해요', 'cads123', '저희 두부 세심하게 잘 봐 주셔서 진심으로 감사드립니다 수의사선생님과 간호사 쌤들도 정말 친절하세요 추천합니다!!\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-15', '06:58:32', 96, 'dulyhos'),
	(24, '친절해요', 'disna1234', '새벽 응급으로 갔는데, 상당히 친절합니다. 응급 진료비는 33,000원 입니다..\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-03', '10:04:42', 77, 'gumihos'),
	(25, '다들 알꺼임', 'disna1234', '친절 하고 정확하고\r\n다른병원 다녀본 분들은 아실 겁니다\r\n여기의 의료 비용이 얼마라 저렴한지를요 ㅎ', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-13', '01:39:57', 91, 'dulyhos'),
	(26, '휴 다행', 'fasddra1234', '과잉진료없이 꼼꼼히 잘 봐주세요~ 주변 지인분이 칭찬하셔서 다른 병원에 항상 봐주시던 선생님이 안계셔서 옮기게 됐는대 만족스럽습니다\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-03-25', '11:00:00', 42, 'cloverhos'),
	(27, '감사합니다 윤짱', 'fasddra1234', '조용한 시간에 가서 그런지 집에 다른고양이 궁금한 부분도 친절히 조언해주셨어요 시간을 넘 뺏은게 아닐지 죄송했는데 편하게 상담해주셔서 고마웠습니다\r\n무엇보다 고양이가 선생님을 심하게 거부하지 않아 다행스러웠어요 가벼운 감기증상이라 진료에 대한부분은 평가가 어렵지만 병원찾으며 고심하고 검색해서 결정하고 첫방문했는데 선생님에 대한 느낌이 좋았습니다\r\n집에서 좀 멀지만 계속 여기 다니고 싶습니다\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-12', '21:19:11', 79, 'docteryun'),
	(28, '이야아', 'taehee1234', '집앞에 24시간 병원이 있어 굉장히 편합니다. 선생님들하고 스탭분들도 다 친절합니다\r\n', '2023-05-21', 0, 5, '앵무새2.jpg', 'B', NULL, '2023-03-25', '17:30:00', 40, 'haddenhos'),
	(29, '여기는 인정', 'taehee1234', '거친 길생활의 후유증으로 잦은 수술 끝에 시청각 장애까지 가지게 된 우리 둘째냥이, 선천적 신장이상까지 발견되어 낙담하고 있었는데 이*용 원장님의 조언이 큰 힘이 되었습니다.\r\n치료 후 둘째는거짓말같이.건강해져서 댕댕이랑 투닥투닥 장난도 치고 온 집안을 우다다다 뛰어다닙니다. 정말 감사드려요.\r\n', '2023-05-21', 0, 5, '앵무새3.jpg', 'B', NULL, '2023-04-11', '15:49:25', 88, 'onehos'),
	(30, '청담점 감사해요', 'kdjnr34', '우리집 반려견이 소장에 염증이 많이 생겨서 계속 토하고 피똥싸고 해서 4일간 입원을 하게되었습니다. 매일 병문안을 가고 슬퍼하고 사람과 똑같은것 같네요. 약물치료하고 치료용 습식사료 먹이고 하여 퇴원하였습니다. 친절하고 매일 사진과 경과에 대한 자세한 정보를 톡으로 보내줘서 안심할수 있었습니다. 감사합니다. 앞으로도 계속해서 반려동물을 잘치료해 주시기 바랍니다.\r\n', '2023-05-21', 0, 5, NULL, 'N', NULL, '2023-04-10', '15:09:46', 59, 'viphos'),
	(31, '어이가없네요 정말', 'seungmu1234', '최고. 친절하고 기본관리 서비스로 다해주시고 평일인데도 아이들이많더라구요 만족이예요^^ 병원에서즉석사진도찍어서서물해주셨어요♡\r\n', '2023-05-21', 0, 5, '드워프햄스터2.jpg', 'B', NULL, '2023-03-21', '16:30:00', 20, 'healhos'),
	(32, '여기가 진짜에요^^', 'seungmu1234', '햄스터가 갑자기 아파서 갔는데 응급으로 바로수술 해주셨어요~회복될때까지 잘 캐어해주시고 선생님들이 모두 친절하십니다.다만 주차가 불편하였습니다.\r\n', '2023-05-21', 0, 5, '드워프햄스터4.jpg', 'B', NULL, '2023-03-26', '10:30:00', 45, 'anyanghos1'),
	(33, '짱짱짱', 'seungmu1234', '울콩이 주치의 나무 원장님이시죠 친철하시구 잘봐주시고 넘고마워요\r\n', '2023-05-21', 0, 4, '드워프햄스터3.jpg', 'N', '신고부당', '2023-03-21', '15:30:00', 21, 'mayhos'),
	(34, '강츄강츄 가격은 하하', 'jtscb543', '우리 강쥐가 피부염이 아주 너무 을심해서 병원을 다닌후 완전히 다 나았어요 강츄~ 강츄~\r\n', '2023-05-21', 0, 3, NULL, 'C', NULL, '2023-04-09', '08:20:19', 98, 'suYhos'),
	(35, '꼼꼼해요', 'st4dbw', '강아지 안검염으로 진료를 봤는데 여자원장님께서 설명 엄청 꼼꼼하게 해주시고(의학적인 부분을 일반인도 알기쉽게) 친절하세요 한번 진료보고 믿음이 팍팍 가더라구요 분당 판교쪽 동물병원 여러군데 가봤는데 대구에서 경기도쪽 이사온지 3년만에 마음에 드는 동물병원을 알게된거같네요\r\n', '2023-05-21', 0, 5, NULL, 'B', NULL, '2023-04-04', '13:36:31', 58, 'cloverhos'),
	(36, '나쁘지 않아요 다만...', 'soyeon1234', '친절하고 병원 시설도 좋고 다 좋지만 그만큼 가격은 싸진 않아요 +-+ 중성화 하고 오셔서 고단\r\n', '2023-05-21', 0, 3, '스피치7.jpg', 'C', NULL, '2023-03-21', '11:30:00', 19, 'treehos'),
	(37, '그냥 좋은거같아요', 'soyeon1234', '애완동물은 처음이라 다른 병원들을 몰라 비교는 힘들어요.\r\n친절하고 좋은데 가격은 조금 부담이 되는데 다른 곳과 비교가 안돼서 상대적으로 비싼지는 모르겠습니다.', '2023-05-21', 0, 5, '닥스훈트2.jpg', 'B', NULL, '2023-03-21', '14:30:00', 26, '24global'),
